mapper.select:
SELECT

FROM YAGL_ORGANIZATION_YA t
WHERE t.DELETE_FLAG = 'N' AND  t.uuid = #{value}

mapper.insert:
INSERT INTO YAGL_ORGANIZATION_YA(uuid,jgid,yaid,delete_flag,datasource,sjc,jdh,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.yaid,jdbcType=VARCHAR} yaid,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.sjc,jdbcType=DATE} sjc,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_ORGANIZATION_YA
uuid=#{uuid}, jgid=#{jgid}, yaid=#{yaid}, delete_flag='N', datasource=#{datasource}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), jdh=#{jdh}, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE uuid = #{uuid}

mapper.select:
SELECT t.uuid,t.jgid,t.yaid FROM YAGL_ORGANIZATION_YA t
LEFT JOIN sys_organization s ON t.jgid = s.uuid
WHERE t.delete_flag = 'N' AND t.yaid = #{value}
