mapper.insert:
INSERT INTO YAGL_WL_XFCLXX(uuid,clbm,clmc,ssdz,ssdzmc,xzqh,cllx,cphm,clzt,sccj,jglgd,sbll,zsl,xfpll,sbedyl,czmhjlb,czmhjl,mhjhhb,gpsbh,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4,gis_x,gis_y) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.clbm,jdbcType=VARCHAR} clbm,#{item.clmc,jdbcType=VARCHAR} clmc,#{item.ssdz,jdbcType=VARCHAR} ssdz,#{item.ssdzmc,jdbcType=VARCHAR} ssdzmc,#{item.xzqh,jdbcType=VARCHAR} xzqh,#{item.cllx,jdbcType=DOUBLE} cllx,#{item.cphm,jdbcType=VARCHAR} cphm,#{item.clzt,jdbcType=VARCHAR} clzt,#{item.sccj,jdbcType=VARCHAR} sccj,#{item.jglgd,jdbcType=DOUBLE} jglgd,#{item.sbll,jdbcType=DOUBLE} sbll,#{item.zsl,jdbcType=DOUBLE} zsl,#{item.xfpll,jdbcType=DOUBLE} xfpll,#{item.sbedyl,jdbcType=DOUBLE} sbedyl,#{item.czmhjlb,jdbcType=VARCHAR} czmhjlb,#{item.czmhjl,jdbcType=DOUBLE} czmhjl,#{item.mhjhhb,jdbcType=VARCHAR} mhjhhb,#{item.gpsbh,jdbcType=VARCHAR} gpsbh,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4,#{item.gisX,jdbcType=DOUBLE} gis_x,#{item.gisY,jdbcType=DOUBLE} gis_y FROM DUAL
) A

mapper.update:
UPDATE YAGL_WL_XFCLXX
uuid=#{uuid}, clbm=#{clbm}, clmc=#{clmc}, ssdz=#{ssdz}, ssdzmc=#{ssdzmc}, xzqh=#{xzqh}, cllx=#{cllx}, cphm=#{cphm}, clzt=#{clzt}, sccj=#{sccj}, jglgd=#{jglgd}, sbll=#{sbll}, zsl=#{zsl}, xfpll=#{xfpll}, sbedyl=#{sbedyl}, czmhjlb=#{czmhjlb}, czmhjl=#{czmhjl}, mhjhhb=#{mhjhhb}, gpsbh=#{gpsbh}, delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), gis_x=#{gisX}, gis_y=#{gisY}
WHERE PKID = #{pkid}

mapper.select:
SELECT , c4.CODE_NAME cllxmc, c9.DZMC ssdzmc FROM YAGL_WL_XFCLXX t
Left Join SYS_CODELIST c3 on c3.CODETYPE = 'CLLX' And c3.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c4 On c3.CODEID = c4.CODEID And c4.DELETE_FLAG = 'N' and t.CLLX = c4.CODE_VALUE
LEFT JOIN YAGL_XFDZ c9 ON (t.SSDZ=c9.DZID AND c9.DELETE_FLAG = 'N') WHERE t.DELETE_FLAG = 'N'
and t.gis_X > #{gisX_min}
and t.gis_X < #{gisX_max}
and t.gis_Y > #{gisY_min}
and t.gis_Y < #{gisY_max}
ORDER BY CLLX,SSDZ,CPHM ASC
