sql.if:
AND t.jgid IN (
SELECT UUID
FROM SYS_ORGANIZATION t
START WITH t.UUID = #{jgid}
CONNECT BY PRIOR t.UUID = SJJGID
)

mapper.select:
SELECT
,
c2.code_name jzlxmc
FROM YAGL_DWJZXX t
LEFT JOIN YAGL_DWJZXX_JZL t_jzl ON t.jzid = t_jzl.jzid AND t_jzl.DELETE_FLAG = 'N'

Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZLX' And c1.DELETE_FLAG = 'N'
Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE
WHERE t.DELETE_FLAG = 'N'

	AND
	(t.jzmc LIKE concat(concat('%',#{jzmc}),'%') OR t.jzwz LIKE concat(concat('%',#{jzmc}),'%'))


	AND t.jzlx = #{jzlx}


	AND t_jzl.jzsyxz LIKE concat(#{jzl_jzsyxz},'%')


	AND t_jzl.jzjg = #{jzl_jzjg}


	AND t_jzl.dsgd <= #{jzl_dsgd_max}


	AND t_jzl.dsgd > #{jzl_dsgd_min}


	and t.jdh = #{jdh}

SELECT , c2.code_name jzlxmc FROM YAGL_DWJZXX t LEFT JOIN YAGL_DWJZXX_ZZL t_zzl ON t.jzid = t_zzl.jzid AND t_zzl.DELETE_FLAG = 'N'
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
AND (t.jzmc LIKE concat(concat('%',#{jzmc}),'%') OR t.jzwz LIKE concat(concat('%',#{jzmc}),'%'))
AND t.jzlx = #{jzlx}
AND t_zzl.jzjg = #{zzl_jzjg}
and t.jdh = #{jdh}

SELECT , c2.code_name jzlxmc FROM YAGL_DWJZXX t LEFT JOIN YAGL_DWJZXX_CGL t_cgl ON t.jzid = t_cgl.jzid AND t_cgl.DELETE_FLAG = 'N'
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
AND (t.jzmc LIKE concat(concat('%',#{jzmc}),'%') OR t.jzwz LIKE concat(concat('%',#{jzmc}),'%'))
AND t.jzlx = #{jzlx}
and t.jdh = #{jdh}

SELECT t.uuid uuid, t.dwmc dxmc, t.dwdz dxdz, t.GIS_X gisX, t.GIS_Y gisY, t.XFZRR xfzrr, t.ZBDH zbdh, t.FHDJ fhdj, c9.DZMC xfgx, '重点单位' yadxType FROM YAGL_DX_ZDDW t
LEFT JOIN YAGL_XFDZ c9 ON (t.MHDZID=c9.DZID AND c9.DELETE_FLAG = 'N') WHERE t.DELETE_FLAG = 'N'
and t.dwmc LIKE concat(concat('%',#{dxmc}),'%')
and t.mhdzid IN ( SELECT DZID FROM YAGL_XFDZ t START WITH t.DZBM = #{xfgx} CONNECT BY PRIOR t.DZID = SJDZID )
and t.dwxz = #{dwxz}
and t.xzqh LIKE concat(RTRIM(#{xzqh},0),'%')
and t.fhdj = #{fhdj}
and t.jzfl = #{jzfl}
and t.jdh = #{jdh}
ORDER BY t.XZQH,t.DWXZ,t.DWMC ASC
