mapper.select:
SELECT

FROM YAGL_XFDZ t
WHERE t.DELETE_FLAG = 'N' AND  t.DZID = #{value}

mapper.insert:
INSERT INTO YAGL_XFDZ(dzid,sjdzid,dzlx,dzbm,dzmc,dzjc,dzdz,xzqh,lxr,lxdh,sxdsl,xygbsl,wzgysl,zqrs,zdymc,zdydh,dzcm,dzdh,zqcl,mhjl,sysl,zddwsl,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.dzid,jdbcType=VARCHAR} dzid,#{item.sjdzid,jdbcType=VARCHAR} sjdzid,#{item.dzlx,jdbcType=VARCHAR} dzlx,#{item.dzbm,jdbcType=VARCHAR} dzbm,#{item.dzmc,jdbcType=VARCHAR} dzmc,#{item.dzjc,jdbcType=VARCHAR} dzjc,#{item.dzdz,jdbcType=VARCHAR} dzdz,#{item.xzqh,jdbcType=VARCHAR} xzqh,#{item.lxr,jdbcType=VARCHAR} lxr,#{item.lxdh,jdbcType=VARCHAR} lxdh,#{item.sxdsl,jdbcType=DOUBLE} sxdsl,#{item.xygbsl,jdbcType=DOUBLE} xygbsl,#{item.wzgysl,jdbcType=DOUBLE} wzgysl,#{item.zqrs,jdbcType=DOUBLE} zqrs,#{item.zdymc,jdbcType=VARCHAR} zdymc,#{item.zdydh,jdbcType=VARCHAR} zdydh,#{item.dzcm,jdbcType=VARCHAR} dzcm,#{item.dzdh,jdbcType=VARCHAR} dzdh,#{item.zqcl,jdbcType=DOUBLE} zqcl,#{item.mhjl,jdbcType=DOUBLE} mhjl,#{item.sysl,jdbcType=DOUBLE} sysl,#{item.zddwsl,jdbcType=DOUBLE} zddwsl,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_XFDZ
dzid=#{dzid}, sjdzid=#{sjdzid}, dzlx=#{dzlx}, dzbm=#{dzbm}, dzmc=#{dzmc}, dzjc=#{dzjc}, dzdz=#{dzdz}, xzqh=#{xzqh}, lxr=#{lxr}, lxdh=#{lxdh}, sxdsl=#{sxdsl}, xygbsl=#{xygbsl}, wzgysl=#{wzgysl}, zqrs=#{zqrs}, zdymc=#{zdymc}, zdydh=#{zdydh}, dzcm=#{dzcm}, dzdh=#{dzdh}, zqcl=#{zqcl}, mhjl=#{mhjl}, sysl=#{sysl}, zddwsl=#{zddwsl}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE DZID = #{dzid}
