mapper.select:

SELECT 

FROM YAGL_YAXXZL t 
WHERE t.JLZT = '1' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO YAGL_YAXXZL(id,yaid,xgxx,zlmc,czyid,czyxm,jlzt,cszt,sjc,bz,fjlxdm,jgid,sjbb,cjsj,ywxtbsid,sltfjdz) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.yaid,jdbcType=VARCHAR} yaid,#{item.xgxx,jdbcType=VARCHAR} xgxx,#{item.zlmc,jdbcType=VARCHAR} zlmc,#{item.czyid,jdbcType=VARCHAR} czyid,#{item.czyxm,jdbcType=VARCHAR} czyxm,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.fjlxdm,jdbcType=VARCHAR} fjlxdm,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.cjsj,jdbcType=DATE} cjsj,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.sltfjdz,jdbcType=VARCHAR} sltfjdz FROM DUAL
) A

mapper.update:
UPDATE YAGL_YAXXZL
id=#{id}, yaid=#{yaid}, xgxx=#{xgxx}, zlmc=#{zlmc}, czyid=#{czyid}, czyxm=#{czyxm}, jlzt=#{jlzt}, cszt=#{cszt}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, fjlxdm=#{fjlxdm}, jgid=#{jgid}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, sltfjdz=#{sltfjdz}
WHERE PKID = #{pkid}

